import styles from "css/App.module.css";
import Nav from "components/Nav";
import Footer from "components/Footer";
import FormOfNoticeDetail from "components/FormOfNoticeDetail";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

function NoticeDetail() {
  const [posts, setPosts] = useState([]);
  const { _id } = useParams();

  const getPosts = () => {
    axios.get("/api/posting").then((response) => {
      let post = response.data;
      setPosts(post);
    });
  };

  const filteredNoticeDetail = posts.filter((item) => {
    return item._id === _id;
  });

  const handleDelete = (e) => {
    axios.delete(`/api/postings/${_id}`).then(console.log("삭제완료!"));
  };

  useEffect(() => {
    getPosts();
  }, []);

  return (
    <div>
      <Nav />
      <h2 className={styles.page_title}>NOTICE DETAIL</h2>
      {filteredNoticeDetail.map((item) => {
        return (
          <FormOfNoticeDetail
            key={item.id}
            _id={item._id}
            title={item.title}
            date={item.date.substring(0, 10)}
            writer={item.writer}
            content={item.content}
            delBtnValue={item._id}
            deleteOnClick={handleDelete}
          />
        );
      })}
      <Footer />
    </div>
  );
}
export default NoticeDetail;
