import { Link, useNavigate } from "react-router-dom";
import styles from "css/App.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faDroplet, faBars } from "@fortawesome/free-solid-svg-icons";
import { useEffect, useState } from "react";
import axios from "axios";

function Nav() {
  const navigate = useNavigate();
  const [toggleBtn, setToggleBtn] = useState(false);
  const [isLogin, setIsLogin] = useState(false);
  const [userName, setUserName] = useState("");

  const handleToggleBtn = () => {
    setToggleBtn(!toggleBtn);
  };

  const getLogin = () => {
    setIsLogin(!isLogin);
  };

  const onClickHandler = () => {
    axios.get("/api/users/logout").then((response) => {
      if (response.data.success) {
        navigate("/");
        alert("Î°úÍ∑∏ÏïÑÏõÉ ÌïòÏòÄÏäµÎãàÎã§.");
        setIsLogin(true);
      } else {
        alert("Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌï©ÎãàÎã§.");
      }
    });
  };

  // const filtered = faqs.filter((faqList) => {
  //   return faqList.q.includes(userInput);
  // });

  const getUserName = () => {
    axios.get("/api/users/username").then((response) => {
      let user = response.data;
      console.log(`Ïú†Ï†ÄÎÑ§ÏûÑ: ${user}`);
      let toUser = toString().response.data;
      console.log(toUser);
      if (user.toString().includes("object")) {
        setUserName("üîêLOG-IN");
        setIsLogin(true);
      } else {
        setUserName(`${user}Îãò üîìLOG-OUT`);
        setIsLogin(false);
      }
    });
  };

  useEffect(() => {
    getUserName();
  }, [isLogin]);

  // useEffect(() => {
  //   //cookieÍ∞ÄÏ†∏Ïò§Í∏∞
  //   let cookie = getCookie('Ïø†ÌÇ§Ïù¥Î¶ÑÎÑ£Í∏∞');
  //   //ÌôïÏù∏
  //   console.log(cookie);
  //   //Ïø†ÌÇ§Í∞Ä ÏûàÏúºÎ©¥?
  //   if(cookie) {
  //     setIsLogin(true)
  //   } else {
  //     setIsLogin(false)
  //   }
  // }, [])

  return (
    <div className={styles.navbar}>
      <div>
        <FontAwesomeIcon icon={faDroplet} className={styles.navbar_svg} />
        <span className={styles.navbar_logo}>
          <Link to={`/`}>Bittersweet</Link>
        </span>
      </div>
      <ul
        className={!toggleBtn ? styles.navbar_menu : styles.navbar_menuToggle}
      >
        <li>
          <Link to={`/`}>HOME</Link>
        </li>
        <li>
          <Link to={`/about`}>ABOUT US</Link>
        </li>
        <li>
          <Link to={`/menu`}>MENU</Link>
        </li>
        <li>
          <Link to={`/store`}>STORE</Link>
        </li>
        <li>
          <Link to={`/faq`}>FAQ</Link>
        </li>
        <li>
          <Link to={`/notice`}>WHAT'S NEW</Link>
        </li>
      </ul>
      {isLogin ? (
        <Link to={`/login`}>
          <button
            className={
              !toggleBtn ? styles.navbar_login : styles.navbar_loginToggle
            }
            onClick={onClickHandler}
          >
            {userName}
          </button>
        </Link>
      ) : (
        <button
          className={
            !toggleBtn ? styles.navbar_logout : styles.navbar_logoutToggle
          }
          onClick={onClickHandler}
        >
          {userName}
        </button>
      )}
      <Link to={`#`}>
        <FontAwesomeIcon
          onClick={handleToggleBtn}
          icon={faBars}
          className={styles.navbar_toggleBtn}
        />
      </Link>
    </div>
  );
}
export default Nav;
