import { useEffect, useState } from "react";
import styles from "../css/App.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCaretDown } from "@fortawesome/free-solid-svg-icons";
import Nav from "../components/Nav";
import Footer from "../components/Footer";

function Faq() {
  const [toggle, setToggle] = useState(false);
  const handleToggle = () => {
    setToggle(!toggle);
  };
  const [question, setQustion] = useState();
  const [answer, setAnswer] = useState();

  const faq = [
    {
      idx: "1",
      q: "첫번째로 자주하는 질문이 뭔가요?",
      a: "첫번째 질문에 대한 대답",
    },
    {
      idx: "2",
      q: "두번째로 자주하는 질문이 뭔가요?",
      a: "두번째 질문에 대한 대답",
    },
    {
      idx: "3",
      q: "세번째로 자주하는 질문이 뭔가요?",
      a: "세번째 질문에 대한 대답",
    },
    {
      idx: "4",
      q: "네번째로 자주하는 질문이 뭔가요?",
      a: "네번째 질문에 대한 대답",
    },
    {
      idx: "5",
      q: "다섯번째로 자주하는 질문이 뭔가요?",
      a: "다섯번째 질문에 대한 대답",
    },
    {
      idx: "6",
      q: "여섯번째로 자주하는 질문이 뭔가요?",
      a: "여섯번째 질문에 대한 대답",
    },
    {
      idx: "7",
      q: "일곱번째로 자주하는 질문이 뭔가요?",
      a: "일곱번째 질문에 대한 대답",
    },
  ];
  const getQuestion = () => {
    setQustion(faq.q);
  };
  const getAnswer = () => {
    setAnswer(faq.a);
  };

  useEffect(() => {
    getQuestion();
    getAnswer();
  }, []);
  console.log(question, answer);

  return (
    <div>
      <Nav />
      <div className={styles.question_container}>
        <div className={styles.question}>
          <button onClick={handleToggle}>
            <span>{faq.q}</span>
            <FontAwesomeIcon
              icon={faCaretDown}
              className={!toggle ? styles.question_i : styles.question_i_rotate}
            />
          </button>
          <p className={!toggle ? styles.question_p : styles.question_p_show}>
            {faq.a}
          </p>
        </div>

        <div className={styles.question}>
          <button onClick={handleToggle}>
            <span>
              Q. 개인 텀블러와 같은 다회용컵 이용 시 할인 금액은 얼마인가요?
            </span>
            <FontAwesomeIcon
              icon={faCaretDown}
              className={!toggle ? styles.question_i : styles.question_i_rotate}
            />
          </button>
          <p className={!toggle ? styles.question_p : styles.question_p_show}>
            A. 개인 텀블러 혹은 컵 등을 지참 후 주문하시면 500원 할인 혜택을
            제공해드립니다.
          </p>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default Faq;
