import styles from "css/App.module.css";
import Nav from "components/Nav";
import Footer from "components/Footer";
import FormOfNoticeDetail from "components/FormOfNoticeDetail";
import { useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

function NoticeDetail() {
  const [posts, setPosts] = useState([]);
  const { _id } = useParams();
  const navigate = useNavigate();

  const getPosts = () => {
    axios.get("/api/posting").then((response) => {
      let post = response.data;
      setPosts(post);
    });
  };

  const filteredNoticeDetail = posts.filter((item) => {
    return item._id === _id;
  });

  useEffect(() => {
    getPosts();
  }, []);

  return (
    <div>
      <Nav />
      <h2 className={styles.page_title}>NOTICE DETAIL</h2>
      {filteredNoticeDetail.map((item) => {
        return (
          <FormOfNoticeDetail
            key={item.id}
            title={item.title}
            date={item.date.substring(0, 10)}
            writer={item.writer}
            content={item.content}
          />
        );
      })}
      <button
        className={styles.detailBtn}
        onClick={() => {
          navigate(-1);
        }}
      >
        목록
      </button>
      <button
        className={styles.detailBtn}
        onClick={() => {
          navigate(-1);
        }}
      >
        수정
      </button>
      <Footer />
    </div>
  );
}
export default NoticeDetail;
