import styles from "css/App.module.css";

function FormOfNoticeWrite({
  formOnSubmit,
  writerPlaceholder,
  writerOnchange,
  writerValue,
  titlePlaceholder,
  titleOnchange,
  titleValue,
  contentPlaceholder,
  contentOnchange,
  contentValue,
  buttonOnSubmit,
  cancleBtnOnClick,
}) {
  return (
    <div className={styles.wrapNoticeWrite}>
      <div>
        <form onSubmit={formOnSubmit}>
          <br />
          <input
            placeholder={writerPlaceholder}
            type="text"
            required="required"
            max="10"
            className={styles.titleInput}
            onChange={writerOnchange}
            value={writerValue}
          ></input>
          <input
            placeholder={titlePlaceholder}
            type="text"
            required="required"
            max="10"
            className={styles.titleInput}
            onChange={titleOnchange}
            value={titleValue}
          ></input>
          <div className={styles.contentWrap}>
            <textarea
              cols="50"
              rows="10"
              placeholder={contentPlaceholder}
              onChange={contentOnchange}
              value={contentValue}
            ></textarea>
          </div>
          <div className={styles.noticeWriteBtnWrap}>
            <button
              type="submit"
              className={styles.postBtn}
              onSubmit={buttonOnSubmit}
            >
              등록
            </button>
            <button className={styles.postCancleBtn} onClick={cancleBtnOnClick}>
              취소
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
export default FormOfNoticeWrite;
