import Nav from "../components/Nav";
import Footer from "../components/Footer";
import styles from "../css/App.module.css";
import FormOfMenu from "../components/FormOfMenu";
import menuData from "../data/menuData.json";
import { useEffect, useState } from "react";

function Menu() {
  const [menus, setMenus] = useState([]);
  const [activeCate, setActiveCate] = useState(categories);
  const categories = ["ALL", "COFFEE", "BEVERAGE", "TEA"];

  const getMenus = () => {
    setMenus(menuData.data);
  };

  const clickCate = (btn) => {
    if (btn === "ALL") {
      setMenus(menus);
      return;
    }
    const filterData = menus.filter((data) => data.category === btn);
    setMenus(filterData);
  };

  useEffect(() => {
    getMenus();
  }, []);

  return (
    <div>
      <Nav />
      <h2 className={styles.page_title}>MENU</h2>
      <div className={styles.menuCateBtnWrap}>
        {activeCate.map((cate) => {
          return <button onClick={() => clickCate(cate)}>{cate}</button>;
        })}
      </div>
      <div className={styles.menu}>
        <ul>
          {menus.map((menu) => {
            return (
              <FormOfMenu
                key={menu.idx}
                idx={menu.idx}
                menuName={menu.name_ko}
              />
            );
          })}
        </ul>
      </div>
      <Footer />
    </div>
  );
}

export default Menu;
