import Nav from "components/Nav";
import Footer from "components/Footer";
import styles from "css/App.module.css";
import FormOfNotice from "components/FormOfNotice";
import { Link, useParams } from "react-router-dom";
import axios from "axios";
import { useState, useEffect } from "react";

function Notice() {
  const [posts, setPosts] = useState([]);
  const [counts, setCounts] = useState(0);
  const { _id } = useParams();

  const getPosts = () => {
    axios.get("/api/posting").then((response) => {
      let post = response.data;
      console.log(post);
      setPosts(post);
    });
  };

  // const filteredId = posts.filter((item) => {
  //   return item._id === _id;
  // });

  const getId = () => {
    axios.get("/api/posting/").then((response) => {
      posts.filter((item) => {
        if (item._id === _id) {
          return console.log(response.data);
        }
      });
    });
  };

  // const getCounts = () => {
  //   posts.filter((item) => {
  //     if (item._id === _id) {
  //       return setCounts(counts + 1);
  //     }
  //   });
  // };

  useEffect(() => {
    getPosts();
  }, []);

  return (
    <div>
      <Nav />
      <h2 className={styles.page_title}>NOTICE</h2>
      <div>
        <Link to={`/notice-write`}>
          <button className={styles.writeBtn}>WRITE</button>
        </Link>
      </div>
      <div className={styles.noticeDiv}>
        <table className={styles.noticeTable}>
          <colgroup>
            <col width="5%" />
            <col width="50%" />
            <col width="20%" />
            <col width="18%" />
            <col width="*" />
          </colgroup>
          <thead>
            <tr>
              <th>NO</th>
              <th>제목</th>
              <th>작성자</th>
              <th>날짜</th>
              <th>조회수</th>
            </tr>
          </thead>
          {posts.map((item) => {
            return (
              <FormOfNotice
                key={item.id}
                _id={item._id}
                no={item.id}
                title={item.title}
                onClick={getId}
                date={item.date.substring(0, 10)}
                writer={item.writer}
                view={counts}
              />
            );
          })}
        </table>
      </div>
      <Footer />
    </div>
  );
}
export default Notice;
