import Nav from "../components/Nav";
import Footer from "../components/Footer";
import styles from "../css/App.module.css";
import FormOfMenu from "../components/FormOfMenu";
import menuData from "../data/menuData.json";
import { useEffect, useState } from "react";
import BtnOfMenu from "../components/BtnOfMenu";

function Menu() {
  const [menus, setMenus] = useState([]);
  const [activeCate, setActiveCate] = useState("ALL");
  const categories = ["ALL", "COFFEE", "BEVERAGE", "TEA"];

  const getMenus = () => {
    setMenus(menuData.data);
  };

  useEffect(() => {
    activeCate === "ALL"
      ? getMenus()
      : getMenus(menus.filter((item) => item.category === activeCate));
  }, [activeCate]);

  return (
    <div>
      <Nav />
      <h2 className={styles.page_title}>MENU</h2>
      {categories.map((btn) => {
        return <BtnOfMenu key={id} cateName={btn} />;
      })}
      <div className={styles.menu}>
        <ul>
          {menus.map((menu) => {
            return (
              <FormOfMenu
                key={menu.idx}
                idx={menu.idx}
                menuName={menu.name_ko}
              />
            );
          })}
        </ul>
      </div>
      <Footer />
    </div>
  );
}

export default Menu;
