import styles from "../css/App.module.css";
import coffee from "../img/menu-coffee.jpg";
import tea from "../img/menu-tea.jpg";
import beverage from "../img/menu-beverage.jpg";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import menuData from "../data/menuData.json";

function FormOfMenu({ menuName, idx }) {
  const dataCate = menuData.data.category;
  const selectImg = () => {
    if (menuData.data.category.includes("COFFEE")) {
      return coffee;
    } else if (dataCate.includes("TEA")) {
      return tea;
    } else {
      return beverage;
    }
  };

  return (
    <div className={styles.menu_div}>
      <li>
        <Link to={`/menu-detail/${idx}`}>
          <img src={selectImg()} alt="menuImg" />
        </Link>
      </li>
      <span>{menuName}</span>
    </div>
  );
}

FormOfMenu.propTypes = {
  idx: PropTypes.string.isRequired,
  menuName: PropTypes.string.isRequired,
};

export default FormOfMenu;
