import styles from "css/App.module.css";
import PropTypes from "prop-types";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function FormOfNoticeDetail({ title, writer, content, date, _id }) {
  const navigate = useNavigate();
  const deleteOnClick = () => {
    axios.post("/api/posting/delete").then(console.log("삭제완료!"));
  };

  return (
    <section className={styles.noticeDetail}>
      <header>
        <ul>
          <li className={styles.detailTitle}>{title}</li>
          <li className={styles.detailWriter}>{writer}</li>
          <li className={styles.detailDate}>{date}</li>
        </ul>
      </header>
      <article>
        <p>{content}</p>
      </article>
      <div className={styles.noticeDetailBtnWrap}>
        <button
          onClick={() => {
            navigate(-1);
          }}
        >
          목록
        </button>
        <button
          className={styles.noticeDetail_updateBtn}
          onClick={() => {
            navigate(`/notice-edit/${_id}`);
          }}
        >
          수정
        </button>
        <button onClick={deleteOnClick}>삭제</button>
      </div>
    </section>
  );
}

FormOfNoticeDetail.propTypes = {
  title: PropTypes.string.isRequired,
  content: PropTypes.string.isRequired,
  date: PropTypes.string.isRequired,
};

export default FormOfNoticeDetail;
