import { useEffect, useState } from "react";
import styles from "../css/App.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCaretDown } from "@fortawesome/free-solid-svg-icons";
import Nav from "../components/Nav";
import Footer from "../components/Footer";
import FormOfFaq from "../components/FormOfFaq";

function Faq() {
  const [faqs, setFaqs] = useState([]);

  const faq = [
    {
      idx: "1",
      q: "첫번째로 자주하는 질문이 뭔가요?",
      a: "첫번째 질문에 대한 대답",
    },
    {
      idx: "2",
      q: "두번째로 자주하는 질문이 뭔가요?",
      a: "두번째 질문에 대한 대답",
    },
    {
      idx: "3",
      q: "세번째로 자주하는 질문이 뭔가요?",
      a: "세번째 질문에 대한 대답",
    },
    {
      idx: "4",
      q: "네번째로 자주하는 질문이 뭔가요?",
      a: "네번째 질문에 대한 대답",
    },
    {
      idx: "5",
      q: "다섯번째로 자주하는 질문이 뭔가요?",
      a: "다섯번째 질문에 대한 대답",
    },
    {
      idx: "6",
      q: "여섯번째로 자주하는 질문이 뭔가요?",
      a: "여섯번째 질문에 대한 대답",
    },
    {
      idx: "7",
      q: "일곱번째로 자주하는 질문이 뭔가요?",
      a: "일곱번째 질문에 대한 대답",
    },
  ];
  const getFaqs = () => {
    setFaqs(faq);
  };

  useEffect(() => {
    getFaqs();
  }, []);
  console.log(faqs);

  return (
    <div>
      <Nav />
      <div className={styles.question_container}>
        {faqs.map((item) => (
          <FormOfFaq key={item.idx} id={item.idx} q={item.q} a={item.a} />
        ))}
      </div>
      <Footer />
    </div>
  );
}

export default Faq;
