import { Link } from "react-router-dom";
import styles from "../css/App.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faDroplet, faBars } from "@fortawesome/free-solid-svg-icons";
import { useState } from "react";

function Nav() {
  const [activeBtn, setActiveBtn] = useState(false);
  const handleToggleBtn = () => {
    setActiveBtn(!activeBtn);
  };

  return (
    <div className={styles.navbar}>
      <div>
        <FontAwesomeIcon icon={faDroplet} className={styles.navbar_svg} />
        <span className={styles.navbar_logo}>
          <Link to={`/`}>Bittersweet</Link>
        </span>
      </div>
      <ul
        className={!activeBtn ? styles.navbar_menu : styles.navbar_menuToggle}
      >
        <li>
          <Link to={`/`}>HOME</Link>
        </li>
        <li>
          <Link to={`/about`}>ABOUT US</Link>
        </li>
        <li>
          <Link to={`/menu`}>MENU</Link>
        </li>
        <li>
          <Link to={`/store`}>STORE</Link>
        </li>
        <li>
          <Link to={`/faq`}>FAQ</Link>
        </li>
        <li>
          <Link to={`/notice`}>WHAT'S NEW</Link>
        </li>
      </ul>
      <span
        className={!activeBtn ? styles.navbar_login : styles.navbar_loginToggle}
      >
        <Link to={`/`}>Sign In</Link>
      </span>
      <Link to={`#`}>
        <FontAwesomeIcon
          onClick={handleToggleBtn}
          icon={faBars}
          className={styles.navbar_toggleBtn}
        />
      </Link>
    </div>
  );
}
export default Nav;
