import {
  LOGIN_USER,
  REGISTER_USER,
  AUTH_USER,
  USER_POSTING,
  USER_EDITING,
} from "_actions/types";

export default function (state = {}, action) {
  switch (action.type) {
    case LOGIN_USER:
      return { ...state, loginSuccess: action.payload };
      break;
    case REGISTER_USER:
      return { ...state, register: action.payload };
      break;
    case AUTH_USER:
      return { ...state, userData: action.payload };
      break;
    case USER_POSTING:
      return { ...state, postSuccess: action.payload };
      break;
    case USER_EDITING:
      return { ...state, editSuccess: action.payload };
    default:
      return state;
  }
}
