import Nav from "../components/Nav";
import Footer from "../components/Footer";
import styles from "../css/App.module.css";
import FormOfMenu from "../components/FormOfMenu";
import menuData from "../data/menuData.json";
import { useEffect, useState } from "react";

function Menu() {
  const [menus, setMenus] = useState([]);
  const [filteredCategory, setFilteredCategory] = useState([]);

  const buttons = [
    {
      name: "ALL",
      value: "ALL",
    },
    {
      name: "COFFEE",
      value: "COFFEE",
    },
    {
      name: "BEVERAGE",
      value: "BEVERAGE",
    },
    {
      name: "TEA",
      value: "TEA",
    },
  ];

  const getMenus = () => {
    setMenus(menuData.data);
  };
  const handleCate = (e) => {
    let clickedBtn = e.target.value;
    console.log(clickedBtn);
    clickedBtn !== "ALL"
      ? setFilteredCategory(filterMenu(clickedBtn))
      : setFilteredCategory(getMenus());
  };

  const filterMenu = (menuCate) => {
    let filteredMenu = getMenus().filter((menu) => menu.category === menuCate);
    return filteredMenu;
  };

  useEffect(() => {
    setFilteredCategory(getMenus());
  }, []);

  return (
    <div>
      <Nav />
      <h2 className={styles.page_title}>MENU</h2>
      <div className={styles.menuCateBtnWrap}>
        {buttons.map((btn, idx) => {
          return (
            <button
              className={styles.cateBtn}
              key={idx}
              onClick={handleCate}
              value={btn.value}
            >
              {btn.name}
            </button>
          );
        })}
        {/* <BtnOfMenu cateName="ALL" handleCate={setActiveCate} />
        <BtnOfMenu cateName="COFFEE" handleCate={setActiveCate} />
        <BtnOfMenu cateName="BEVERAGE" handleCate={setActiveCate} />
        <BtnOfMenu cateName="TEA" handleCate={setActiveCate} /> */}
      </div>
      <div className={styles.menu}>
        <ul>
          {menus.map((menu) => {
            return (
              <FormOfMenu
                key={menu.idx}
                idx={menu.idx}
                menuName={menu.name_ko}
              />
            );
          })}
        </ul>
      </div>
      <Footer />
    </div>
  );
}

export default Menu;
