import styles from "../css/App.module.css";
import coffee from "../img/menu-cappuccino.jpg";
// import tea from "../img/menu-tea.jpg";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import menuData from "../data/menuData.json";
import { useState } from "react";

function FormOfMenu({ menuName, idx }) {
  const [cateImg, setCateImg] = useState();

  return (
    <div className={styles.menu_div}>
      <li>
        <Link to={`/menu-detail/${idx}`}>
          <img src={coffee} alt="coffeeImg" />
        </Link>
      </li>
      <span>{menuName}</span>
    </div>
  );
}

FormOfMenu.propTypes = {
  idx: PropTypes.string.isRequired,
  menuName: PropTypes.string.isRequired,
};

export default FormOfMenu;
