import Nav from "../components/Nav";
import Footer from "../components/Footer";
import styles from "../css/App.module.css";
import FormOfMenu from "../components/FormOfMenu";
import menuData from "../data/menuData.json";
import { useEffect, useState } from "react";

function Menu() {
  const [menus, setMenus] = useState([]);

  const getMenus = () => {
    setMenus(menuData.data);
  };

  const categories = ["COFFEE", "BEVERAGE", "TEA"];

  // const categoryClick = () => {};

  // const categoryFilter = menus.filter((datas) => {
  //   if (datas.category === "COFFEE") {
  //     return (
  //       categoryFilter.map((menuList) => {
  //         return (
  //           <FormOfMenu
  //               key={menuList.idx}
  //               idx={menuList.idx}
  //               menuName={menuList.name_ko}
  //           />
  //         )
  //       }
  //     )
  //   }

  // });

  useEffect(() => {
    getMenus();
  }, []);

  return (
    <div>
      <Nav />
      <h2 className={styles.page_title}>MENU</h2>
      <div className={styles.menuCateBtnWrap}>
        {categories.map((item) => {
          return <button>{item}</button>;
        })}
      </div>
      <div className={styles.menu}>
        <ul>
          {menus.map((menu) => {
            return (
              <FormOfMenu
                key={menu.idx}
                idx={menu.idx}
                menuName={menu.name_ko}
              />
            );
          })}
        </ul>
      </div>
      <Footer />
    </div>
  );
}

export default Menu;
