import Nav from "../components/Nav";
import Footer from "../components/Footer";
import styles from "../css/App.module.css";
import FormOfMenu from "../components/FormOfMenu";
import menuData from "../data/menuData.json";
import { useEffect, useState } from "react";
import BtnOfMenu from "../components/BtnOfMenu";

function Menu() {
  const [menus, setMenus] = useState([]);
  const [cates, setCates] = useState([]);
  const [filteredCategory, setFilteredCategory] = useState(null);

  const categories = [
    {
      idx: "1",
      cate: "ALL",
    },
    {
      idx: "2",
      cate: "COFFEE",
    },
    {
      idx: "3",
      cate: "BEVERAGE",
    },
    {
      idx: "4",
      cate: "TEA",
    },
  ];

  const getMenus = () => {
    setMenus(menuData.data);
  };
  const handleCate = (e) => {
    let menuCate = e.target.value;
    console.log(menuCate);
    menuCate !== "ALL"
      ? setFiltered(filtered(menuCate))
      : setFiltered(getMenus());
  };
  const getCates = () => {
    setCates(categories.cate);
  };

  const filterCategory = (menuCate) => {
    let filterdCategory = getMenus().filter(
      (datas) => datas.category === menuCate
    );
    return filterdCategory;
  };

  useEffect(() => {
    getMenus();
  }, []);

  return (
    <div>
      <Nav />
      <h2 className={styles.page_title}>MENU</h2>
      <div className={styles.menuCateBtnWrap}>
        {cates.map((btn) => {
          return (
            // <BtnOfMenu key={btn.idx} cateName={btn.cate} onClick={handleCate} />
            <button
              className={styles.cateBtn}
              key={btn.idx}
              onClick={handleCate}
            >
              {btn.cate}
            </button>
          );
        })}
        {/* <BtnOfMenu cateName="ALL" handleCate={setActiveCate} />
        <BtnOfMenu cateName="COFFEE" handleCate={setActiveCate} />
        <BtnOfMenu cateName="BEVERAGE" handleCate={setActiveCate} />
        <BtnOfMenu cateName="TEA" handleCate={setActiveCate} /> */}
      </div>
      <div className={styles.menu}>
        <ul>
          {menus.map((menu) => {
            return (
              <FormOfMenu
                key={menu.idx}
                idx={menu.idx}
                menuName={menu.name_ko}
              />
            );
          })}
        </ul>
      </div>
      <Footer />
    </div>
  );
}

export default Menu;
